export default {
  addApp ({ commit }, text) {
    commit('addApp', {
      text,
      done: false
    })
  },

  removeApp ({ commit }, app) {
    commit('removeApp', app);
    console.log(this);
    // let nmb = 20;
    // let apps = document.querySelectorAll('.appointments').length;
    // apps *= nmb;
    // console.log(apps)
    // if(apps < 100){
    //   console.log(this.completedSteps, 'cpmpl')
    //   this.completedSteps = apps;
    // }else{
    //   console.log(this.completedSteps, 'cp')
    //   this.completedSteps = '100'
    // }
  },

  toggleApp ({ commit }, app) {
    commit('editApp', { app, done: !app.done })
  },

  editApp ({ commit }, { app, value }) {
    commit('editApp', { app, text: value })
  },

  toggleAll ({ state, commit }, done) {
    state.apps.forEach((app) => {
      commit('editApp', { app, done })
    })
  },

  clearCompleted ({ state, commit }) {
    state.apps.filter(app => app.done)
      .forEach(app => {
        commit('removeApp', app)
      })
  }
}
