import { Line } from 'vue-chartjs';
export default {
  name: 'chart-line',
  extends: Line,
  props: {
    data: {
      type: Object,
      default: null
    },
    options: {
      type: Object,
      default: null
    }
  },
  watch: {
    data: function () {
      this.update();
    },
    options: function () {
      this.update();
    }
  },
  mounted: function () {
    this.update();
  },
  methods: {
    update: function () {
      this.renderChart(this.data, this.options);
    }
  }
};
